mapper.select:
    select 
    fc.ID, fc.organizationname, fc.organizationid, fc.cargotype, fc.cargoname, fc.cargono, fc.zjm, fc.status, fc.payType,
    fc.measure, fc.specifications, fc.model, fc.imagepath, fc.creator, fc.createtime, fc.modifier, fc.modifytime, 
    fc.DESC1, fc.DESC2, fc.DESC3, fc.DESC4 ,
    (select count(*) from file_org_cargo oc where oc.cargoid = fc.ID) count
      from file_cargo fc


        and fc.ID = #{id,jdbcType=VARCHAR}


        and fc.organizationname = #{organizationname,jdbcType=VARCHAR}


        and fc.organizationid = #{organizationid,jdbcType=VARCHAR}


        and fc.cargotype = #{cargotype,jdbcType=VARCHAR}


        and fc.cargoname like "%" #{cargoname,jdbcType=VARCHAR} "%"


        and fc.cargono like "%" #{cargono,jdbcType=VARCHAR} "%"


        and fc.zjm = #{zjm,jdbcType=VARCHAR}


        and fc.status = #{status,jdbcType=VARCHAR}


        and fc.payType = #{payType,jdbcType=VARCHAR}


        and fc.measure = #{measure,jdbcType=VARCHAR}


        and fc.specifications = #{specifications,jdbcType=VARCHAR}


        and fc.model = #{model,jdbcType=VARCHAR}


        and fc.imagepath = #{imagepath,jdbcType=VARCHAR}


        and fc.creator = #{creator,jdbcType=VARCHAR}


        and fc.createtime = #{createtime,jdbcType=BIGINT}


        and fc.modifier = #{modifier,jdbcType=VARCHAR}


        and fc.modifytime = #{modifytime,jdbcType=BIGINT}


        and fc.DESC1 = #{desc1,jdbcType=VARCHAR}


        and fc.DESC2 = #{desc2,jdbcType=VARCHAR}


        and fc.DESC3 = #{desc3,jdbcType=VARCHAR}


        and fc.DESC4 = #{desc4,jdbcType=VARCHAR}


      order by fc.createtime desc

	    limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}


	    limit #{limit,jdbcType=INTEGER}

select 
    count(*)
  from file_cargo


    and ID = #{id,jdbcType=VARCHAR}


    and organizationname = #{organizationname,jdbcType=VARCHAR}


    and organizationid = #{organizationid,jdbcType=VARCHAR}


    and cargotype = #{cargotype,jdbcType=VARCHAR}


    and cargoname = #{cargoname,jdbcType=VARCHAR}


    and cargono = #{cargono,jdbcType=VARCHAR}


    and zjm = #{zjm,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and payType = #{payType,jdbcType=VARCHAR}


    and measure = #{measure,jdbcType=VARCHAR}


    and specifications = #{specifications,jdbcType=VARCHAR}


    and model = #{model,jdbcType=VARCHAR}


    and imagepath = #{imagepath,jdbcType=VARCHAR}


    and creator = #{creator,jdbcType=VARCHAR}


    and createtime = #{createtime,jdbcType=BIGINT}


    and modifier = #{modifier,jdbcType=VARCHAR}


    and modifytime = #{modifytime,jdbcType=BIGINT}


    and DESC1 = #{desc1,jdbcType=VARCHAR}


    and DESC2 = #{desc2,jdbcType=VARCHAR}


    and DESC3 = #{desc3,jdbcType=VARCHAR}


    and DESC4 = #{desc4,jdbcType=VARCHAR}
