mapper.select:
    select 
	    fo.ID, fo.organizationname, fo.organizationid, fo.cargoid, fo.cargoname,
	     fo.cargono, fo.cargostatus, fo.status, 
	    fo.measure, fo.creator, fo.createtime, fo.modifier, fo.modifytime, fo.DESC1, fo.DESC2, fo.DESC3, fo.DESC4,
     	(select count(*) from file_freight ff where ff.cargoid = fo.ID) count
      from file_org_cargo fo


        and ID = #{id,jdbcType=VARCHAR}


        and organizationname = #{organizationname,jdbcType=VARCHAR}


        and organizationid = #{organizationid,jdbcType=VARCHAR}


        and cargoid = #{cargoid,jdbcType=VARCHAR}


        and cargoname like "%" #{cargoname,jdbcType=VARCHAR} "%"


        and cargono like "%" #{cargono,jdbcType=VARCHAR} "%"


        and cargostatus = #{cargostatus,jdbcType=VARCHAR}


        and status = #{status,jdbcType=VARCHAR}


        and measure = #{measure,jdbcType=VARCHAR}


        and creator = #{creator,jdbcType=VARCHAR}


        and createtime = #{createtime,jdbcType=VARCHAR}


        and modifier = #{modifier,jdbcType=VARCHAR}


        and modifytime = #{modifytime,jdbcType=VARCHAR}


        and DESC1 = #{desc1,jdbcType=VARCHAR}


        and DESC2 = #{desc2,jdbcType=VARCHAR}


        and DESC3 = #{desc3,jdbcType=VARCHAR}


        and DESC4 = #{desc4,jdbcType=VARCHAR}


   		order by createtime desc

	    limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}


	    limit #{limit,jdbcType=INTEGER}

select 
    count(*)
  from file_org_cargo


    and ID = #{id,jdbcType=VARCHAR}


    and organizationname = #{organizationname,jdbcType=VARCHAR}


    and organizationid = #{organizationid,jdbcType=VARCHAR}


    and cargoid = #{cargoid,jdbcType=VARCHAR}


    and cargoname like "%" #{cargoname,jdbcType=VARCHAR} "%"


    and cargono like "%" #{cargono,jdbcType=VARCHAR} "%"


    and cargostatus = #{cargostatus,jdbcType=VARCHAR}


    and status = #{status,jdbcType=VARCHAR}


    and measure = #{measure,jdbcType=VARCHAR}


    and creator = #{creator,jdbcType=VARCHAR}


    and createtime = #{createtime,jdbcType=VARCHAR}


    and modifier = #{modifier,jdbcType=VARCHAR}


    and modifytime = #{modifytime,jdbcType=VARCHAR}


    and DESC1 = #{desc1,jdbcType=VARCHAR}


    and DESC2 = #{desc2,jdbcType=VARCHAR}


    and DESC3 = #{desc3,jdbcType=VARCHAR}


    and DESC4 = #{desc4,jdbcType=VARCHAR}
