mapper.select:
SELECT p.id AS planId, r.id AS routeId, r.routename AS routeName FROM wuliu_plan p LEFT JOIN file_route r ON p.routeid = r.id
and p.id in
#{itemId}

select f.ID, f.organizationname, f.organizationid, f.routename, f.status, f.oPositionId, f.oAddr, f.dPositionId, f.dAddr, f.sendPersion, f.sendPersionPhone, f.receivePersion, f.receivePersionPhone, f.distance, f.creator, f.createtime, f.modifier, f.modifytime, (select count(*) from file_freight t where t.routeid = f.ID) count from file_route f
and f.organizationid = #{organizationid,jdbcType=VARCHAR}
and f.routename like concat("%",#{routename,jdbcType=VARCHAR},"%")
and f.status = #{status,jdbcType=VARCHAR}
order by f.createtime desc
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select 
	count(1)
from file_route


   and  organizationid = #{organizationid,jdbcType=VARCHAR}


   and  routename like concat("%",#{routename,jdbcType=VARCHAR},"%")


   and  status = #{status,jdbcType=VARCHAR}
