mapper.select:
SELECT f.ID ID, f.freightName freightName, f. STATUS STATUS, i.ID infoid, f.tallage tallage, i.tallage tallageInfo, f.price price, i.price priceInfo, f.freightType freightType, i.freightType freightTypeInfo, f.auditstatus auditstatus, f.auditreason auditreason, f.priceUnits priceunits, c.cargoname cargoid, c. STATUS DESC3, r.routename routeid, r. STATUS DESC4, m.measureName measure, (select count(*) from wuliu_plan p where p.freightid = f.id) count FROM file_freight f LEFT JOIN file_org_cargo c ON c.ID = f.cargoid LEFT JOIN file_route r ON r.ID = f.routeid LEFT JOIN wuliu_measure m ON m.ID = f.measure LEFT JOIN file_freight_info i ON i.freightid = f.ID AND i.recent = 1
AND f. STATUS != 2
and f.organizationid = #{organizationid,jdbcType=VARCHAR}
and f.cargoid in (select ID from file_org_cargo where cargoname like "%" #{cargoid,jdbcType=VARCHAR} "%")
and f.routeid in (select ID from file_route where routename like "%" #{routeid,jdbcType=VARCHAR} "%")
and f.freightName like "%" #{freightName,jdbcType=VARCHAR} "%"
and f.auditstatus = #{auditstatus,jdbcType=VARCHAR}
ORDER BY f.createtime DESC
limit #{limit,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    select 
    *
    from file_freight_info


     and freightid = #{freightid,jdbcType=VARCHAR}


     and status = #{status,jdbcType=VARCHAR}


     and recent = #{recent,jdbcType=VARCHAR}

    ORDER BY
		createtime
