mapper.select:
SELECT f.ID ID, f.freightName freightName, f. STATUS STATUS, i.ID infoid, f.taketime taketime, f.tallage tallage, f.oldtallage oldtallage, i.tallage tallageInfo, f.oldprice oldprice, f.price price, i.price priceInfo, f.freightType freightType, i.freightType freightTypeInfo, f.auditstatus auditstatus, f.auditreason auditreason, f.priceUnits priceunits, i.taketime taketimeinfo, c.cargoname cargoid, c. STATUS DESC3, r.routename routeid, r. STATUS DESC4, i.desc1 desc1, m.measureName measure, (select count(*) from wuliu_plan p where p.freightid = f.id) count FROM file_freight f LEFT JOIN file_org_cargo c ON c.ID = f.cargoid LEFT JOIN file_route r ON r.ID = f.routeid LEFT JOIN wuliu_measure m ON m.ID = f.measure LEFT JOIN file_freight_info i ON i.freightid = f.ID AND i.recent = 1
AND f. STATUS != 2
and f.organizationid = #{organizationid,jdbcType=VARCHAR}
and f.cargoid in (select ID from file_org_cargo where cargoname like "%" #{cargoid,jdbcType=VARCHAR} "%")
and f.routeid in (select ID from file_route where routename like "%" #{routeid,jdbcType=VARCHAR} "%")
and f.freightName like "%" #{freightName,jdbcType=VARCHAR} "%"
and f.auditstatus = #{auditstatus,jdbcType=VARCHAR}
ORDER BY f.createtime DESC
limit #{limit,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    select 
    *
    from file_freight_info


     and freightid = #{freightid,jdbcType=VARCHAR}


     and desc4 is null


     and status = #{status,jdbcType=VARCHAR}


     and recent = #{recent,jdbcType=VARCHAR}


    and taketime <= #{taketime,jdbcType=BIGINT}


    ORDER BY
		taketime

mapper.update:
update file_freight_info 
	set desc4 = #{desc4,jdbcType=DOUBLE}
where taketime >= #{taketime,jdbcType=BIGINT}
	and status = 1
	and recent = 0

update wuliu_plan
set price = #{price,jdbcType=DOUBLE},
  type = #{freighttype,jdbcType=VARCHAR}
where freightid = #{freightid,jdbcType=VARCHAR}
	and status in (0,2)
	and createtime > #{taketime,jdbcType=BIGINT}
