SELECT vender_id FROM pay_invoice_detail_1
AND creator = #{creator,jdbcType=VARCHAR}
AND bill_type = #{billType,jdbcType=BIT}
GROUP BY vender_id

select p.id, p. CODE, p.pay_invoice_id, p.driver_id, p.driver_id_no, p.vender_id, p.vender_id_no, p.owner_id, p.owner_id_no, p.invoice_code, p.invoice_name, p.bill_id, p.bill_code, p.bill_type, p.material_code, p.material_name, p.cargo_name, p.bill_weight, p.bill_price, p.tax_rate, p.reception_bill_total_price, p.backstage_bill_total_price, p.reception_deduct_weight_misc, p.reception_deduct_money, p.reception_deduct_other, p.reception_deduct_oil_card, p.backstage_deduct_weight_misc, p.backstage_deduct_money, p.backstage_deduct_other, p.backstage_deduct_oil_card, p.whether_close, p.orgid, p.orgname, p.state, p.creator, p.create_time, p.modifier, p.modify_time, p.remark, p.appendix, p.billweightB, p.billpriceB, p.memo, dm.cellphone driverPhone, dm.remarkname driverName, vm.cellphone venderPhone, vm.remarkname venderName, wp.shipperMerchant, fm.`name` shipname, afm.`name` consignee, ab.bankOwnerName albankOwnerName, ab.bankOwnerPhone albankOwnerPhone, wb.bankOwnerName dybankOwnerName, wb.bankOwnerPhone dybankOwnerPhone FROM pay_invoice_detail_1 p LEFT JOIN member dm ON dm.id = p.driver_id LEFT JOIN member vm ON vm.id = p.vender_id LEFT JOIN wuliu_plan wp ON wp.ID = p.planId LEFT JOIN file_merchant fm ON wp.shipperMerchant = fm.id LEFT JOIN file_merchant afm ON wp.consigneeMerchant = afm.id LEFT JOIN anlian_bill ab ON ab.id = p.bill_id LEFT JOIN wuliu_bill wb ON wb.ID = p.bill_id
and p.creator = #{creator,jdbcType=VARCHAR}
and p.create_time >= #{timeBegin,jdbcType=BIGINT}
and p.create_time <= #{timeEnd,jdbcType=BIGINT}
and fm.name like "%" #{shipname,jdbcType=VARCHAR} "%"
and afm.name like "%" #{consignee,jdbcType=VARCHAR} "%"
and (p.code like "%" #{searchKey,jdbcType=VARCHAR} "%" or p.cargo_name like "%" #{searchKey,jdbcType=VARCHAR} "%")
and p.backstage_bill_total_price = 0 and p.whether_close = 0
and p.backstage_bill_total_price != 0 and p.whether_close = 0
and p.whether_close = 1
and p.whether_close = #{whetherClose,jdbcType=BIT}
and p.invoice_name like "%" #{invoiceName,jdbcType=VARCHAR} "%"
and p.code like "%" #{code,jdbcType=VARCHAR} "%"
and p.cargo_name like "%" #{cargoName,jdbcType=VARCHAR} "%"
and (dm.remarkname like "%" #{driverName,jdbcType=VARCHAR} "%" or ab.bankOwnerName like "%" #{driverName,jdbcType=VARCHAR} "%" or wb.bankOwnerName like "%" #{driverName,jdbcType=VARCHAR} "%")
and (dm.cellphone like "%" #{driverPhone,jdbcType=VARCHAR} "%" or wb.bankOwnerPhone like "%" #{driverPhone,jdbcType=VARCHAR} "%" or ab.bankOwnerPhone like "%" #{driverPhone,jdbcType=VARCHAR} "%")
and vm.remarkname like "%" #{venderName,jdbcType=VARCHAR} "%"
and vm.cellphone like "%" #{venderPhone,jdbcType=VARCHAR} "%"
and p.remark = #{remark,jdbcType=VARCHAR}
and p.vender_id = #{venderId,jdbcType=VARCHAR}
and p.bill_type = #{billType,jdbcType=BIT}
and p.pay_invoice_id = #{payInvoiceId,jdbcType=VARCHAR}
and p.bill_id = #{billId,jdbcType=VARCHAR}
and p.id in
#{employeeId}
order by p.create_time desc
limit #{pageNo,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

