mapper.select:
    select 
    ID, name, description, CREATETIME, number, 
     (select count(1) from trwuliu_User_Role where trwuliu_User_Role.ROLEID_U =trwuliu_role.id)as DESC4,
     creator, DESC1, DESC2, DESC3
    from trwuliu_role


       and  name = #{name,jdbcType=VARCHAR}


        and   description = #{description,jdbcType=VARCHAR}


        and   number = #{number,jdbcType=VARCHAR}


	order by ID desc


	      	limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

   select 
		count(1)
   from trwuliu_role


      and  name = #{name,jdbcType=VARCHAR}


       and   description = #{description,jdbcType=VARCHAR}


       and   number = #{number,jdbcType=VARCHAR}
