mapper.select:
    select 
        ID, ACCOUNT, PASSWORD, CREATETIME, LOGINCOUNT, totalPurchases, usedTotal, USERTYPE, 
   		 telnum, STATUS, orgid,orgname,
   		 (select count(1) from trwuliu_User_Role where trwuliu_User_Role.USERID =trwuliu_users.id) as DESC1,
   		  DESC2, DESC3, DESC4
    from trwuliu_users


 	  	and DESC4 = #{desc4,jdbcType=VARCHAR}


       and ACCOUNT = #{account,jdbcType=VARCHAR}


       and LOGINCOUNT = #{logincount,jdbcType=INTEGER}


       and USERTYPE = #{usertype,jdbcType=VARCHAR}


       and telnum = #{telnum,jdbcType=VARCHAR}


       and STATUS = #{status,jdbcType=INTEGER}


       and orgid = #{orgid,jdbcType=INTEGER}


    order by ID desc


	      	limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

  select 
		count(1)
  from trwuliu_users


     and ACCOUNT = #{account,jdbcType=VARCHAR}


     and LOGINCOUNT = #{logincount,jdbcType=INTEGER}


     and USERTYPE = #{usertype,jdbcType=VARCHAR}


     and telnum = #{telnum,jdbcType=VARCHAR}


     and STATUS = #{status,jdbcType=INTEGER}


     and orgid = #{orgid,jdbcType=INTEGER}

mapper.delete:
delete from trwuliu_users
where ID in

            #{itemId}
