SELECT ab.`billno`, ab.`cph`, ab.`trueweight`, ab.`signtime`, pi.`pay_status`, pi.`amount_payable`, wp.`planCode`, wp.`cargoname`, wp.`startcity`, wp.`endcity`, fr.`routename` FROM `anlian_bill` ab LEFT JOIN `pay_invoice_detail_1` pid ON ab.`id` = pid.`bill_id` LEFT JOIN `pay_invoice_1` pi ON pid.`pay_invoice_id` = pi.`id` LEFT JOIN `wuliu_plan` wp ON ab.desc1 = wp.`ID` LEFT JOIN `file_route` fr ON wp.routeid = fr.id
and ab.receive_memberid = #{receiveMemberid,jdbcType=VARCHAR}
and ab.driverid = #{driverid,jdbcType=VARCHAR}
and ab.ownerid = #{ownerid,jdbcType=VARCHAR}
and ab.venderid = #{venderid,jdbcType=VARCHAR}
and ab.billno = #{billno,jdbcType=VARCHAR}
and ab.cph = #{vehicleno,jdbcType=VARCHAR}
and fr.routename = #{routename,jdbcType=VARCHAR}
and ab.signtime = #{signtimes,jdbcType=VARCHAR}
and pi.pay_status = #{payStatus,jdbcType=INTEGER}
and (pi.pay_status = #{payStatus,jdbcType=VARCHAR} or pi.pay_status is null)
and wp.planCode = #{planCode,jdbcType=VARCHAR}
order by signtime desc
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

