      and code = #{code,jdbcType=VARCHAR},


      and innercode = #{innercode,jdbcType=VARCHAR}


      and name = #{name,jdbcType=VARCHAR}


      and bank_type_id = #{bankTypeId,jdbcType=VARCHAR}
