mapper.select:
SELECT routeid id, count(0) count FROM wuliu_bill
and createtime >= #{selecttime,jdbcType=BIGINT}
GROUP BY routeid ORDER BY COUNT(0) DESC
limit 0,#{max,jdbcType=INTEGER}

SELECT SUM(totalplanned) AS totalplanned, SUM(completed) AS completed, COUNT(0) AS count FROM wuliu_plan
and STATUS = #{status,jdbcType=VARCHAR}
and createtime >= #{selecttime,jdbcType=BIGINT}

    SELECT 
    	COUNT(0) count
   	FROM wuliu_member_vehicle


	   		and STATUS = #{status,jdbcType=VARCHAR}


			and createtime >= #{selecttime,jdbcType=BIGINT}

SELECT vehicleid id, vehicleno remark, COUNT(0) count FROM wuliu_bill
and STATUS = #{status,jdbcType=VARCHAR}
and createtime >= #{selecttime,jdbcType=BIGINT}
GROUP BY vehicleid ORDER BY COUNT(0) DESC

SELECT COUNT(0) from wuliu_bill
and STATUS in (${status})
and createtime >= #{selecttime,jdbcType=BIGINT}

