SELECT * FROM (SELECT cph as countName, count(cph) as countNum FROM anlian_bill GROUP BY cph ORDER BY count(cph) DESC) a
and a.countNum >= #{min,jdbcType=INTEGER}
and a.countNum < #{max,jdbcType=INTEGER}

