SELECT vd.id id, vd.driverid driverid, vd.vehicleid vehicleid, vd.drivername drivername, vd.drivertel drivertel, vd.vehicleTypeName vehicleType, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel, v.vehilength length, v.vehiweight weight, v.vehicleprefix vehfix, v.vehicleno vehno, v.billstatus billstatus, (-1) status FROM wuliu_vehicle_driver vd LEFT JOIN member_info f ON f.id = vd.creator LEFT JOIN wuliu_member_vehicle v ON v.id = vd.vehicleid
and vd.id = #{id,jdbcType=VARCHAR}
and vd.vehicleno like "%" #{vehicleno,jdbcType=VARCHAR} "%"
and vd.drivername like "%" #{drivername,jdbcType=VARCHAR} "%"
and vd.drivertel like "%" #{drivertel,jdbcType=VARCHAR} "%"
and vd.creator = #{memberid,jdbcType=VARCHAR}
and vd.vehicleid = #{vehicleid,jdbcType=VARCHAR}
UNION ALL SELECT c.id id, d.driverid driverid, d.vehicleid vehicleid, d.drivername drivername, d.drivertel drivertel, v.vehicletypename vehicletype, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel, v.vehilength length, v.vehiweight weight, v.vehicleprefix vehfix, v.vehicleno vehno, v.billstatus billstatus, c.status status FROM wuliu_member_capa c LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id LEFT JOIN member_info f ON f.id = c.ownerid LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid
and c.id = #{id,jdbcType=VARCHAR}
and d.vehicleid is not null
and d.vehicleno like "%" #{vehicleno,jdbcType=VARCHAR} "%"
and d.drivername like "%" #{drivername,jdbcType=VARCHAR} "%"
and d.drivertel like "%" #{drivertel,jdbcType=VARCHAR} "%"
and c.memberid = #{memberid,jdbcType=VARCHAR}
and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}
and c.status = #{status,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

    SELECT
	count(*)
	FROM
		wuliu_vehicle_driver vd
		LEFT JOIN member_info f ON f.id = vd.creator
		LEFT JOIN wuliu_member_vehicle v ON v.id = vd.vehicleid 


			and vd.id = #{id,jdbcType=VARCHAR}


    		and vd.creator = #{memberid,jdbcType=VARCHAR}


    		and vd.vehicleid = #{vehicleid,jdbcType=VARCHAR}

	SELECT
		count(*)
	FROM
		wuliu_member_capa c
		LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id
		LEFT JOIN member_info f ON f.id = c.ownerid
		LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid


			and c.id = #{id,jdbcType=VARCHAR}


    		and c.memberid = #{memberid,jdbcType=VARCHAR}


    		and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}


    		and c.status = #{status,jdbcType=VARCHAR}

SELECT c.id id, d.drivername drivername, d.drivertel drivertel, v.vehicletypename vehicletype, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel, v.vehilength length, v.vehiweight weight, v.vehicleprefix vehfix, v.vehicleno vehno, v.billstatus billstatus, c.status status FROM wuliu_member_capa c LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id LEFT JOIN member_info f ON f.id = c.ownerid LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid
and c.memberid = #{memberid,jdbcType=VARCHAR} and d.vehicleid IS NOT NULL and c.status = 1
and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}
