mapper.select:
SELECT c.id id, c.status status, d.vehicleno vehicleno, d.drivername drivername, d.drivertel drivertel, v.vehicletypename vehicletype, v.billstatus billstatus, v.vehiweight weight, v.vehilength length, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel FROM wuliu_member_capa c LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id LEFT JOIN member_info f ON f.id = c.ownerid LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid
and c.memberid = #{memberid,jdbcType=VARCHAR}
and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select 
count(*)
from wuliu_member_capa


		and memberid = #{memberid,jdbcType=VARCHAR}


		and vehicleid = #{vehicleid,jdbcType=VARCHAR}
