mapper.select:
SELECT vd.id id, vd.drivername drivername, vd.drivertel drivertel, vd.vehicleno vehicleno, vd.vehicleTypeName vehicleType, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel, v.vehilength length, v.vehiweight weight, v.billstatus billstatus, (-1) status FROM wuliu_vehicle_driver vd LEFT JOIN member_info f ON f.id = vd.creator LEFT JOIN wuliu_member_vehicle v ON v.id = vd.vehicleid
and vd.creator = #{memberid,jdbcType=VARCHAR}
and vd.vehicleid = #{vehicleid,jdbcType=VARCHAR}
UNION ALL SELECT c.id id, d.drivername drivername, d.drivertel drivertel, d.vehicleno vehicleno, v.vehicletypename vehicletype, f.username username, f.telphone telphone, f.companyname companyname, f.companytel companytel, v.vehilength length, v.vehiweight weight, v.billstatus billstatus, c.status status FROM wuliu_member_capa c LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id LEFT JOIN member_info f ON f.id = c.ownerid LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid
and c.memberid = #{memberid,jdbcType=VARCHAR}
and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

SELECT count(*) FROM wuliu_vehicle_driver vd LEFT JOIN member_info f ON f.id = vd.creator LEFT JOIN wuliu_member_vehicle v ON v.id = vd.vehicleid
and vd.creator = #{memberid,jdbcType=VARCHAR}
and vd.vehicleid = #{vehicleid,jdbcType=VARCHAR}

SELECT count(*) FROM wuliu_member_capa c LEFT JOIN wuliu_member_vehicle v ON c.vehicleid = v.id LEFT JOIN member_info f ON f.id = c.ownerid LEFT JOIN wuliu_vehicle_driver d ON c.vehicleid = d.vehicleid
and c.memberid = #{memberid,jdbcType=VARCHAR}
and c.vehicleid = #{vehicleid,jdbcType=VARCHAR}
