select 
    count(*)
  from wuliu_member_vehicle


    and id = #{id,jdbcType=VARCHAR}


    and memberid = #{memberid,jdbcType=VARCHAR}


    and vehicleid = #{vehicleid,jdbcType=VARCHAR}


    and vehicleprefix = #{vehicleprefix,jdbcType=VARCHAR}


    and vehicleno = #{vehicleno,jdbcType=VARCHAR}


    and vehicletype = #{vehicletype,jdbcType=VARCHAR}


    and vehicletypename = #{vehicletypename,jdbcType=VARCHAR}


    and vehilength = #{vehilength,jdbcType=DOUBLE}


    and vehiwidth = #{vehiwidth,jdbcType=DOUBLE}


    and vehiheight = #{vehiheight,jdbcType=DOUBLE}


    and vehiweight = #{vehiweight,jdbcType=DOUBLE}



    and status = #{status,jdbcType=VARCHAR}

select a.id, a.memberid, a.vehicleid as vehiid, a.vehicleprefix as vehiprefix, a.vehicleno as vehino, a.vehicletype as vehitype, a.vehicletypename as vehitypename, a.vehilength, a.vehiweight, a.vehiheadimgpath, a.status, a.memo,a.createtime,a.audittime, b.id AS vehidriverid, b.driverid, b.drivername, b.drivertel, a.billstatus,a.vehiOwnerTel as vehiOwnerTel, a.vehiOwnerName as vehiOwnerName,a.vehilicenseimgpath as vehilicenseimgpath, a.registcode as registcode, a.registimage as registimage, a.opercode as opercode, a.operimage as operimage, a.identitycode as identitycode, a.identieyimage as identieyimage, a.agreeimage as agreeimage, a.roadtransportcode as roadtransportcode, a.roadtransportimage as roadtransportimage from wuliu_member_vehicle a left join wuliu_vehicle_driver b on a.memberid = b.creator and a.vehicleid = b.vehicleid
and a.memberid = #{memberid,jdbcType=VARCHAR}
and ( a.vehicletypename like "%"#{search,jdbcType=VARCHAR}"%" or a.vehicleprefix like "%"#{search,jdbcType=VARCHAR}"%" or a.vehicleno like "%"#{search,jdbcType=VARCHAR}"%" )
and a.vehicleprefix like "%"#{searchfix,jdbcType=VARCHAR}"%" and a.vehicleno like "%"#{searchno,jdbcType=VARCHAR}"%"
and b.drivername like "%" #{drivername,jdbcType=VARCHAR}"%"
and b.drivertel like "%" #{drivertel,jdbcType=VARCHAR}"%"
and a.vehicleprefix like "%" #{vehiprefix,jdbcType=VARCHAR}"%"
and a.vehicleno like "%" #{vehino,jdbcType=VARCHAR}"%"
and a.vehicletype = #{vehitype,jdbcType=VARCHAR}
and a.vehilength like "%" #{vehilength,jdbcType=DOUBLE} "%"
order by b.creatTime
limit #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}
limit #{limit,jdbcType=INTEGER}

