	   		and pickup_time >= #{pickupTimeStarts,jdbcType=BIGINT}


			and pickup_time <= #{pickupTimeEnds,jdbcType=BIGINT}


	   		and unload_time >= #{unloadTimeStarts,jdbcType=BIGINT}


