mapper.select:
  	select 
  	from member_info


	    and id = #{id,jdbcType=VARCHAR}


	    and licenseImagePath = #{licenseImagePath,jdbcType=VARCHAR}


	    and memberid = #{memberid,jdbcType=VARCHAR}


	    and submittime = #{submittime,jdbcType=BIGINT}


	    and username = #{username,jdbcType=VARCHAR}


	    and telphone = #{telphone,jdbcType=VARCHAR}


	    and idcard = #{idcard,jdbcType=VARCHAR}


	    and idcardimage = #{idcardimage,jdbcType=VARCHAR}


	    and driverimage = #{driverimage,jdbcType=VARCHAR}


	    and companyname = #{companyname,jdbcType=VARCHAR}


	    and companycontact = #{companycontact,jdbcType=VARCHAR}


	    and companycode = #{companycode,jdbcType=VARCHAR}


	    and companytel = #{companytel,jdbcType=VARCHAR}


	    and companyaddress = #{companyaddress,jdbcType=VARCHAR}


	    and createtime = #{createtime,jdbcType=BIGINT}


	    and rtblno = #{rtblno,jdbcType=VARCHAR}


	    and rtblimgurl = #{rtblimgurl,jdbcType=VARCHAR}


	    and licenseType = #{licenseType,jdbcType=VARCHAR}


	    and sex = #{sex,jdbcType=VARCHAR}


	    and birthday = #{birthday,jdbcType=VARCHAR}


	    and firstlicens = #{firstlicens,jdbcType=VARCHAR}


	    and licenceorg = #{licenceorg,jdbcType=VARCHAR}


	    and starttime = #{starttime,jdbcType=VARCHAR}


	    and usefullife = #{usefullife,jdbcType=VARCHAR}


	    and idcardaddress = #{idcardaddress,jdbcType=VARCHAR}


	    and positive = #{positive,jdbcType=VARCHAR}


	    and opposite = #{opposite,jdbcType=VARCHAR}


	    and pushStatus = #{pushStatus,jdbcType=BIT}


	    and ncStatus = #{ncStatus,jdbcType=BIT}

  	select 
  	from member_info


	    and pushStatus = #{pushStatus,jdbcType=BIT}


	    and ncStatus != #{ncStatus,jdbcType=BIT}
